<?php

namespace Customize\Repository;

use Customize\Entity\BusinessHoliday;
use DateTime;
use Doctrine\Persistence\ManagerRegistry;
use Eccube\Repository\AbstractRepository;

/**
 * BusinessHolidayRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BusinessHolidayRepository extends AbstractRepository
{
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, BusinessHoliday::class);
    }

    /**
     * Get future holidays.
     *
     * @param $year
     * @return array
     */
    public function getFutureHolidays($year): array
    {
        return $this->createQueryBuilder('h')
            ->where('h.holiday_date >= :fromDate')
            ->andWhere('h.holiday_date <= :toDate')
            ->setParameters(['fromDate' => $year . '/01/01', 'toDate' => $year . '/12/31'])
            ->orderBy('h.holiday_date', 'DESC')
            ->getQuery()
            ->getResult();
    }

    /**
     * ○日後の営業日を取得
     *
     * @param $year
     * @return array
     */
    public function getFutureBusinessDay($days)
    {
        $today = new DateTime();

        for($i=0;$i<$days;$i++){
            $today = $today->modify('+1 day');

            //echo $today->format('Y-m-d H:i:s');

            $holiday = $this->createQueryBuilder('h')
                ->where('h.holiday_date = :fromDate')
                ->setParameters(['fromDate' => $today->format('Y/m/d')])
                ->getQuery()
                ->getResult();

            $is_holiday = false;
            if($holiday){
                $is_holiday = true;
            }

            if($today->format("w") == 0){
                $is_holiday = true;
            }

            //休日の場合はループ回数を１回増やす
            if($is_holiday){$i--;}
        }

        return $today;
    }
}